package authenticators

import (
	"fmt"

	"golang.org/x/oauth2"
	"golang.org/x/oauth2/github"
	api_utils "www.velocidex.com/golang/velociraptor/api/utils"
	config_proto "www.velocidex.com/golang/velociraptor/config/proto"
	"www.velocidex.com/golang/velociraptor/constants"
	"www.velocidex.com/golang/velociraptor/json"
	utils "www.velocidex.com/golang/velociraptor/utils"
)

const (
	GithubIcon = "data:image/png;base64,"
)

type GithubOidcRouter struct {
	config_obj *config_proto.Config
}

func (self *GithubOidcRouter) Name() string {
	return "GitHub"
}

func (self *GithubOidcRouter) LoginHandler() string {
	return "/auth/github/login"
}

func (self *GithubOidcRouter) CallbackHandler() string {
	return "/auth/github/callback"
}

func (self *GithubOidcRouter) Scopes() []string {
	return []string{"user:email"}
}

func (self *GithubOidcRouter) Issuer() string {
	return "https://github.com/login/oauth"
}

func (self *GithubOidcRouter) Endpoint() oauth2.Endpoint {
	return github.Endpoint
}

func (self *GithubOidcRouter) SetEndpoint(oauth2.Endpoint) {}

func (self *GithubOidcRouter) Avatar() string {
	return GithubIcon
}

func (self *GithubOidcRouter) LoginURL() string {
	return api_utils.PublicURL(self.config_obj, self.LoginHandler())
}

// Github does not actually support oidc so we need a pure oauth2
// claims getter.

type GitHubUser struct {
	Login     string `json:"login"`
	AvatarUrl string `json:"avatar_url"`
}

type GithubClaimsGetter struct {
	config_obj *config_proto.Config
}

func (self *GithubClaimsGetter) GetClaims(
	ctx *HTTPClientContext, token *oauth2.Token) (claims *Claims, err error) {

	githubOauthConfig := &oauth2.Config{
		Endpoint: github.Endpoint,
	}

	response, err := githubOauthConfig.Client(ctx, token).Get("https://api.github.com/user")
	if err != nil {
		return nil, fmt.Errorf("failed getting user info: %v", err)
	}
	defer response.Body.Close()

	contents, err := utils.ReadAllWithLimit(response.Body, constants.MAX_MEMORY)
	if err != nil {
		return nil, fmt.Errorf("failed read response: %v", err)
	}

	user_info := &GitHubUser{}
	err = json.Unmarshal(contents, &user_info)
	if err != nil {
		return nil, err
	}

	// Update the user picture in the datastore if we can - it
	// will be populated from there for each GetUserUITraits
	// call. This keeps our cookie smaller.
	setUserPicture(ctx, user_info.Login, user_info.AvatarUrl)

	return &Claims{
		Username: user_info.Login,
	}, nil
}
